```python
import json
import requests
from typing import Dict, Any, Optional
from dataclasses import dataclass

@dataclass
class UserData:
    id: int
    name: str
    email: str
    active: bool
    skills: list[str]

class JSONParser:
    def __init__(self, api_url: str):
        self.api_url = api_url
        self.session = requests.Session()
        self.session.headers.update({
            'User-Agent': 'Mozilla/5.0',
            'Accept': 'application/json'
        })

    def fetch_data(self) -> Optional[Dict[Any, Any]]:
        '''Достаем данные из API'''
        try:
            response = self.session.get(self.api_url, timeout=10)
            response.raise_for_status()
            return response.json()
        except requests.exceptions.RequestException as e:
            print(f'🚨 Ошибка запроса: {e}')
            return None
        except json.JSONDecodeError as e:
            print(f'📄 Ошибка парсинга JSON: {e}')
            return None

    def parse_user(self, data: Dict) -> Optional[UserData]:
        '''Парсим конкретного пользователя'''
        try:
            # Валидация обязательных полей
            required_fields = ['id', 'name', 'email']
            for field in required_fields:
                if field not in data:
                    print(f'⚠️ Отсутствует обязательное поле: {field}')
                    return None

            return UserData(
                id=int(data['id']),
                name=str(data['name']).strip(),
                email=str(data['email']).lower(),
                active=bool(data.get('active', True)),
                skills=list(data.get('skills', []))
            )
        except (ValueError, KeyError) as e:
            print(f'🔧 Ошибка преобразования данных: {e}')
            return None

    def process_users(self) -> list[UserData]:
        '''Основной метод обработки'''
        raw_data = self.fetch_data()
        if not raw_data:
            return []

        users = []
        # Обрабатываем разные структуры ответа
        if 'users' in raw_data:
            user_list = raw_data['users']
        elif isinstance(raw_data, list):
            user_list = raw_data
        else:
            user_list = [raw_data]

        for user_data in user_list:
            user = self.parse_user(user_data)
            if user:
                users.append(user)

        print(f'✅ Успешно обработано {len(users)} пользователей')
        return users

# Пример использования
if __name__ == '__main__':
    # Тестовые данные
    mock_data = {
        'users': [
            {
                'id': 1,
                'name': 'Иван Петров',
                'email': 'ivan@example.com',
                'active': True,
                'skills': ['Python', 'SQL', 'Docker']
            },
            {
                'id': 2,
                'name': 'Мария Сидорова', 
                'email': 'maria@test.ru',
                'active': False,
                'skills': ['JavaScript', 'React']
            }
        ]
    }

    # Можно сохранить в файл для теста
    with open('test_data.json', 'w', encoding='utf-8') as f:
        json.dump(mock_data, f, ensure_ascii=False, indent=2)

    parser = JSONParser('file://test_data.json')
