from fastapi import FastAPI, HTTPException, Depends, status
from fastapi.security import HTTPBearer, HTTPAuthorizationCredentials
from pydantic import BaseModel, EmailStr, validator
from typing import List, Optional
import uuid
from datetime import datetime

# Модели данных
class UserCreate(BaseModel):
    username: str
    email: EmailStr
    full_name: str

    @validator('username')
    def validate_username(cls, v):
        if len(v) < 3:
            raise ValueError('Username должен быть не менее 3 символов')
        if not v.isalnum():
            raise ValueError('Username должен содержать только буквы и цифры')
        return v

class UserResponse(BaseModel):
    id: str
    username: str
    email: str
    full_name: str
    created_at: datetime
    is_active: bool

class PostCreate(BaseModel):
    title: str
    content: str

    @validator('title')
    def validate_title(cls, v):
        if len(v) < 5:
            raise ValueError('Заголовок должен быть не менее 5 символов')
        return v

class PostResponse(BaseModel):
    id: str
    title: str
    content: str
    author_id: str
    created_at: datetime

# Заглушка для базы данных
class Database:
    def __init__(self):
        self.users = {}
        self.posts = {}
        self.tokens = {'secret-token-123': 'admin'}

    def create_user(self, user_data: dict) -> str:
        user_id = str(uuid.uuid4())
        user_data.update({
            'id': user_id,
            'created_at': datetime.now(),
            'is_active': True
        })
        self.users[user_id] = user_data
        return user_id

    def get_user(self, user_id: str) -> Optional[dict]:
        return self.users.get(user_id)

    def get_user_by_username(self, username: str) -> Optional[dict]:
        for user in self.users.values():
            if user['username'] == username:
                return user
        return None

    def create_post(self, post_data: dict) -> str:
        post_id = str(uuid.uuid4())
        post_data.update({
            'id': post_id,
            'created_at': datetime.now()
        })
        self.posts[post_id] = post_data
        return post_id

    def get_user_posts(self, user_id: str) -> List[dict]:
        return [post for post in self.posts.values() if post['author_id'] == user_id]

    def validate_token(self, token: str) -> bool:
        return token in self.tokens

# Инициализация
app = FastAPI(
    title='Blog API',
    description='Простое API для блога с FastAPI',
    version='1.0.0',
    docs_url='/docs',
    redoc_url='/redoc'
)

security = HTTPBearer()
db = Database()

# Зависимости
async def get_current_user(credentials: HTTPAuthorizationCredentials = Depends(security)):
    token = credentials.credentials
    if not db.validate_token(token):
        raise HTTPException(
            status_code=status.HTTP_401_UNAUTHORIZED,
            detail='Неверный токен авторизации',
            headers={'WWW-Authenticate': 'Bearer'}
        )
    return token

# Роуты
@app.get('/', tags=['Health'])
async def root():
    return {
        'message': 'Blog API работает!',
        'version': '1.0.0',
        'docs': '/docs',
        'timestamp': datetime.now()
    }

@app.post('/users/', response_model=UserResponse, tags=['Users'])
async def create_user(user: UserCreate):
    '''Создание нового пользователя'''

    # Проверяем уникальность username
    if db.get_user_by_username(user.username):
        raise HTTPException(
            status_code=status.HTTP_400_BAD_REQUEST,
            detail='Пользователь с таким username уже существует'
        )

    user_data = user.dict()
    user_id = db.create_user(user_data)

    created_user = db.get_user(user_id)
    return UserResponse(**created_user)

@app.get('/users/{user_id}', response_model=UserResponse, tags=['Users'])
async def get_user(user_id: str):
    '''Получение пользователя по ID'''
    user = db.get_user(user_id)
    if not user:
        raise HTTPException(
            status_code=status.HTTP_404_NOT_FOUND,
            detail='Пользователь не найден'
        )
    return UserResponse(**user)

@app.post('/posts/', response_model=PostResponse, tags=['Posts'])
async def create_post(
    post: PostCreate,
    current_user: str = Depends(get_current_user)
):
    '''Создание нового поста (требует авторизации)'''

    # В реальном приложении здесь была бы логика получения user_id из токена
    author_id = list(db.users.keys())[0] if db.users else None

    if not author_id:
        raise HTTPException(
            status_code=status.HTTP_400_BAD_REQUEST,
            detail='Нет пользователей для создания поста'
        )

    post_data = post.dict()
    post_data['author_id'] = author_id

    post_id = db.create_post(post_data)
    created_post = db.posts[post_id]

    return PostResponse(**created_post)

@app.get('/users/{user_id}/posts', response_model=List[PostResponse], tags=['Posts'])
