import pytest
import unittest
from unittest.mock import Mock, patch, MagicMock
from datetime import datetime

# Тестируемый код
class Calculator:
    def add(self, a: float, b: float) -> float:
        '''Сложение двух чисел'''
        if not isinstance(a, (int, float)) or not isinstance(b, (int, float)):
            raise ValueError('Аргументы должны быть числами')
        return a + b

    def divide(self, a: float, b: float) -> float:
        '''Деление двух чисел'''
        if not isinstance(a, (int, float)) or not isinstance(b, (int, float)):
            raise ValueError('Аргументы должны быть числами')
        if b == 0:
            raise ZeroDivisionError('Деление на ноль!')
        return a / b

    def complex_calculation(self, x: float) -> float:
        '''Сложный расчет с внешней зависимостью'''
        # Имитируем вызов внешнего API
        external_service = ExternalService()
        multiplier = external_service.get_multiplier()
        return x * multiplier

class ExternalService:
    def get_multiplier(self) -> float:
        '''Внешний сервис, который мы будем мокать'''
        # В реальности здесь был бы HTTP запрос
        return 2.0

# Тесты с unittest
class TestCalculatorUnittest(unittest.TestCase):
    def setUp(self):
        '''Настройка перед каждым тестом'''
        self.calc = Calculator()
        print('🔧 Подготовка тестового окружения...')

    def tearDown(self):
        '''Очистка после каждого теста'''
        print('🧹 Очистка после теста...')

    def test_add_positive_numbers(self):
        '''Тест сложения положительных чисел'''
        result = self.calc.add(2, 3)
        self.assertEqual(result, 5)
        self.assertIsInstance(result, float)

    def test_add_negative_numbers(self):
        '''Тест сложения отрицательных чисел'''
        result = self.calc.add(-2, -3)
        self.assertEqual(result, -5)

    def test_add_invalid_input(self):
        '''Тест с некорректными входными данными'''
        with self.assertRaises(ValueError):
            self.calc.add('2', 3)

    def test_divide_normal(self):
        '''Тест обычного деления'''
        result = self.calc.divide(10, 2)
        self.assertEqual(result, 5.0)

    def test_divide_by_zero(self):
        '''Тест деления на ноль'''
        with self.assertRaises(ZeroDivisionError):
            self.calc.divide(10, 0)

# Тесты с pytest
class TestCalculatorPytest:
    @pytest.fixture
    def calculator(self):
        '''Фикстура - переиспользуемый объект для тестов'''
        return Calculator()

    @pytest.mark.parametrize('a,b,expected', [
        (1, 1, 2),
        (0, 0, 0),
        (-1, 1, 0),
        (2.5, 3.5, 6.0),
    ])
    def test_add_parameterized(self, calculator, a, b, expected):
        '''Параметризованный тест сложения'''
        assert calculator.add(a, b) == expected

    @pytest.mark.parametrize('a,b,expected,exception', [
        (10, 2, 5.0, None),
        (10, 0, None, ZeroDivisionError),
        ('10', 2, None, ValueError),
    ])
    def test_divide_comprehensive(self, calculator, a, b, expected, exception):
        '''Комплексный тест деления с разными сценариями'''
        if exception:
            with pytest.raises(exception):
                calculator.divide(a, b)
        else:
            assert calculator.divide(a, b) == expected

    @pytest.mark.slow
    def test_complex_calculation_with_mock(self, calculator):
        '''Тест с моком внешней зависимости'''
        # Создаем мок для ExternalService
        mock_service = Mock(spec=ExternalService)
        mock_service.get_multiplier.return_value = 3.0

        # Заменяем реальный сервис моком
        with patch('__main__.ExternalService', return_value=mock_service):
            result = calculator.complex_calculation(5)

        assert result == 15.0
        mock_service.get_multiplier.assert_called_once()

# Фикстуры для тестовых данных
@pytest.fixture
def sample_user_data():
    '''Фикстура с тестовыми данными пользователя'''
    return {
        'username': 'test_user',
